import React from "react";

import Todo from "../Todo/Todo";
import TodosNav from "../TodosNav/TodosNav";
import "./Todos.css";

const uniqId = require("uniqid");

const Todos = props => {
  console.log(props.listForDisplay.id);
  if (
    props.listForDisplay !== undefined ||
    props.listForDisplay.id !== undefined
  ) {
    var displayTodo = props.listForDisplay.todos.map(el => {
      return (
        <Todo
          key={uniqId()}
          todo={el}
          toggleDone={props.toggleDone}
          deleteTodo={props.deleteTodo}
        />
      );
    });
  }

  return (
    <div>
      <TodosNav
        createNewTodo={props.createNewTodo}
        todoValue={props.todoValue}
        changeTodoValue={props.changeTodoValue}
      />
      <span>할일이 여기에 표시됩니다.</span>
      <ul>{displayTodo}</ul>
    </div>
  );
};

export default Todos;
