import React from "react";
import "./Todo.css";

const Todo = props => {
  // console.log(props);
  const clickCheckHandle = () => {
    // console.log(props);
    props.toggleDone(props.todo);
  };

  const clickDeleteHandle = () => {
    props.deleteTodo(props.todo.id);
  };

  let className;
  props.todo.done === false
    ? (className = "unchecked")
    : (className = "checked");

  return (
    <li>
      <p className={className}>{props.todo.todo}</p>
      <button onClick={clickCheckHandle}>체크</button>
      <button onClick={clickDeleteHandle}>삭제</button>
    </li>
  );
};

export default Todo;
